mapper.select:
SELECT COUNT(m_id) 
FROM member 
WHERE m_id= #{m_id}

SELECT COUNT(m_email) 
FROM member 
WHERE m_email= #{m_email}

mapper.insert:
INSERT INTO member (m_id, m_pw, m_name, nickname, birth, gender, m_zipcode, m_addr1, m_addr2, m_tel, m_email, mem_grade, point, regdate)
VALUES (
		 #{m_id}
		 ,#{m_pw}
		 ,#{m_name}
		 ,#{nickname}
		 ,#{birth}
		 ,#{gender}
		 ,#{m_zipcode}
		 ,#{m_addr1}
		 ,#{m_addr2}
		 ,#{m_tel}
		 ,#{m_email}
		 ,'B'
		 ,'0'
		 ,(current_date)
		)
